document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll('[data-toggle="modal"]').forEach(e=>{e.addEventListener("click",()=>{const t=e.getAttribute("data-target"),o=document.querySelector(t);o&&o.classList.add("show")})}),document.querySelectorAll(".modal").forEach(e=>{e.querySelectorAll('[data-dismiss="modal"]').forEach(t=>{t.addEventListener("click",()=>e.classList.remove("show"))}),e.addEventListener("click",t=>{t.target===e&&e.classList.remove("show")})}),document.addEventListener("keydown",e=>{e.key==="Escape"&&document.querySelectorAll(".modal.show").forEach(t=>t.classList.remove("show"))})});
