document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("[data-temporary-upload-toggle]").forEach(a=>{a.addEventListener("click",i=>{const n=a.checked,o=a.getAttribute("data-for-media-manager"),t=document.querySelector("#"+o).querySelector(".media-manager-config"),r=t.value;try{const e=JSON.parse(r);e.temporary_upload=n,n&&!e.temporary_upload_uuid&&(e.temporary_upload_uuid="dummy-non-uuid"),t.value=JSON.stringify(e)}catch(e){console.warn("Something went wrong while reading or updating JSON",e)}})})});
