document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("[data-temporary-upload-toggle]").forEach(a=>{a.addEventListener("click",c=>{const t=a.checked,o=a.getAttribute("data-for-media-manager"),n=document.querySelector("#"+o).querySelector(".media-manager-config"),r=n.value;try{const e=JSON.parse(r);e.temporary_upload=t,n.value=JSON.stringify(e)}catch(e){console.warn("Something went wrong while reading or updating JSON",e)}})})});
