const o=document.getElementById("insert-selected");o.addEventListener("click",()=>{const e=window.parent?.tinymce;if(!e){console.error("TinyMCE not found on window.parent");return}const t=Array.from(document.querySelectorAll("[data-mle-media-select-checkbox]:checked")).map(n=>({url:n.dataset.url,alt:n.dataset.alt||"",vspace:"1rem",hspace:"1rem",border:0,borderstyle:"none"}));if(!t.length){e.activeEditor.windowManager.alert("Please select one image.");return}if(t.length>1){e.activeEditor.windowManager.alert("Please select only one image.");return}const r=t[0];window.close(),window.parent.postMessage({mce:!0,content:r},"*")});
