const a=document.getElementById("insert-selected");a.addEventListener("click",()=>{var n;const e=(n=window.parent)==null?void 0:n.tinymce;if(!e){console.error("TinyMCE not found on window.parent");return}const t=Array.from(document.querySelectorAll(".mle-media-select-checkbox:checked")).map(r=>({url:r.dataset.url,alt:r.dataset.alt||"",vspace:"1rem",hspace:"1rem",border:0,borderstyle:"none"}));if(!t.length){e.activeEditor.windowManager.alert("Please select one image.");return}if(t.length>1){e.activeEditor.windowManager.alert("Please select only one image.");return}const o=t[0];window.close(),window.parent.postMessage({mce:!0,content:o},"*")});
